                if (!([[tempDic valueForKey:@"dealCategory"] rangeOfString:@"turn"].location == NSNotFound)) {
                    
                    turnOverDealVal = [[NSMutableArray alloc]init];
                    
                    NSArray *arr = [tempDic valueForKey:@"rangeList"];
                    
