 * @date
 * @method       checkMaxReturnAmountWith
 * @author
 * @param        float
 * @param        BOOL
 * @param
 * @param
 * @param
 * @return       BOOL
 *
 * @modified By  Srinivasulu on 18/11/2017....
 * @reason       added the comments....
 *
 * @verified By
 * @verified On
 *
 */

- (BOOL)checkMaxReturnAmountWith:(float)incrementAmount isTextField:(BOOL)isTextField {
    if (!isTextField) {
        if ((returnedval.text).floatValue == 0) {
            if ((returnAmtVal.text).floatValue >= incrementAmount) {
                return YES;
            }
            return NO;
        }
        else {
            if (((returnedval.text).floatValue + incrementAmount) > (returnAmtVal.text).floatValue) {
                return NO;
            }
            return YES;
        }
        
    }
    else {
        if ((returnedval.text).floatValue == 0) {
            if ((returnAmtVal.text).floatValue >= incrementAmount) {
                return YES;
            }
            return NO;
        }
        else {
            if ( incrementAmount > (returnAmtVal.text).floatValue) {
                return NO;
            }
            return YES;
        }
        
    }
    return NO;
}

