                    }
                    else{
                        
                        float count = [categoriesArr[self.selectIndex.section] count]/2.0;
                        
                        
                        if (count == 0) {
                            
                            return 110;
                        }
                        else {
                            return 160*ceil(@(count).doubleValue);
                        }
                        
                        
                    }
                    
                    
                    
                }
            }
            else {
                if (indexPath.row == 0) {
