    //upto here on 13/09/2017....
    
    
    for (int i = 0; i < cartTotalItems.count; i++) {
        NSArray *itemDetails = [cartTotalItems[i] componentsSeparatedByString:@"#"];
        [skuIdList addObject:itemDetails[0]];
