                    if ([taxDic.allKeys containsObject:taxTypeKey] && ![[taxDic valueForKey:taxTypeKey] isKindOfClass:[NSNull class]] && [[[taxDic valueForKey:taxTypeKey] lowercaseString] isEqualToString:@"percentage"]) {
                        
