 */

-(void)textViewDidBeginEditing:(UITextView *)textView {
    
    @try {
        
        offSetViewTo = textView.frame.origin.y - textView.frame.size.height;
        
        [self keyboardWillShow];
    } @catch (NSException *exception) {
        
    } @finally {
        
    }
    
}

