-(BOOL)saveCustomerLoyaltyCardsInfo:(NSMutableDictionary *)loyaltyDetailsDic {
    
    NSDate *today = [NSDate date];
    NSDateFormatter *f = [[NSDateFormatter alloc] init];
    f.dateFormat = @"dd/MM/yyyy HH:mm:ss";
