                                }
                            }
                            else {
                                mProductOfferPrice[p.intValue] = @(tempValue * ([comboIndividualQtyList[p.intValue] intValue] * [itemsUnitPriceList[p.intValue] floatValue]));
                                
                            }
                            
                            
                            
                            mProductDealQty[p.intValue] = @0;
