                            appliedOfferIdsArr[p.intValue] = applyOffer.offerID;
                            
                            NSString *offerType = applyOffer.rewardType;
                            mProductOfferType[p.intValue] = offerType;
                        }
                        isComboApplied = true;
                        
                    }
                }
                
                
            } @catch (NSException *e) {
                
                NSLog(@"%@",e);
