                                } else if (mtotalBill - totalOfferDiscountGlobal
                                           - ((toGiveDealQuantity - givenDealQty)
                                              * sku.price) > (minPurchaseAmount_low)) {
                                               turnOverDeal = turnOverDeal
                                               + ((toGiveDealQuantity - givenDealQty) * sku.price);
