                        groupTurnOverOffer += [[applyDealsAndOfferResponse valueForKey:@"groupTurnOverOfferDiscount"] floatValue];
                        applyDealsAndOfferResponse[@"groupTurnOverOfferDiscount"] = @(groupTurnOverOffer);
                    }//end of isToProceed....
