                else{
                    
                    if(itemPrice >= startRange){
                        
                        float totalOfferPrice = (itemPrice * itemQty) - (rewardValue * itemQty);
                        
                        mProductOfferPrice[index] = @(totalOfferPrice);
                        discountIdArrayList[index] = offerIdStr;
                        discountTypeArrayList[index] = @"Offer";
