    mConnesctionState = WisePadConnectionState_scanning;
    NSString* txt =@"scanning devices";
    [self setWisePadStateSequnce :txt step:0];
    
    // [(UILabel*)[viewDeviceListPicker viewWithTag:(5)] setText:@"searching for devices..."];
    
    return YES;
}
